# Helper: parse date + time into Python datetime object
def parse_datetime(date_str, time_str):
    return frappe.utils.get_datetime(f"{date_str} {time_str}")

if doc.shift:
    shift = frappe.get_doc("Shift Type", doc.shift)
    frappe.msgprint(f"shift.start_time {shift.start_time}")
    frappe.msgprint(f"shift.end_time {shift.end_time}")

    if shift.start_time and shift.end_time:
        work_start = parse_datetime(doc.attendance_date, shift.start_time)
        work_end = parse_datetime(doc.attendance_date, shift.end_time)

        # Calculate late entry
        if doc.in_time:
            in_time = frappe.utils.get_datetime(doc.in_time)
            late_minutes = max((in_time - work_start).total_seconds() / 60, 0)
            doc.custom_late_entry_in_minutes = round(late_minutes, 1)

        # Calculate early exit
        if doc.out_time:
            out_time = frappe.utils.get_datetime(doc.out_time)
            early_minutes = max((work_end - out_time).total_seconds() / 60, 0)
            doc.custom_early_exit_in_minutes = round(early_minutes, 1)
    else:
        frappe.msgprint("Shift Type is missing start or end time.")
else:
    frappe.msgprint("Please select a Shift first.")

try:
